let sidebar_close_btn=document.getElementById("sidebar_close_btn"),sidebar_open_btn=document.getElementById("sidebar_open_btn"),backdrop=document.getElementById("backdrop"),sidebarContainer=document.getElementById("sidebar"),nav=document.getElementById("nav");function close_sidebar(){sidebarContainer.classList.contains("active")&&(backdrop.classList.remove("active"),sidebarContainer.classList.remove("active"))}sidebar_open_btn.addEventListener("click",(function(){sidebarContainer.classList.contains("active")||(backdrop.classList.add("active"),sidebarContainer.classList.add("active"))})),backdrop.addEventListener("click",close_sidebar),sidebar_close_btn.addEventListener("click",close_sidebar),window.addEventListener("scroll",(function(e){window.scrollY>1?nav.classList.add("sticky"):nav.classList.remove("sticky")}));let backToTopBtn=document.getElementById("back-to-top");backToTopBtn.addEventListener("click",(function(){document.documentElement.scrollTop=0})),window.addEventListener("scroll",(function(){window.scrollY>50?backToTopBtn.style.cssText="right:15px ":backToTopBtn.style.cssText="right:-70px "}));let productCount=parseInt(localStorage.getItem("cartCounts"));document.querySelector("h4.count").textContent=productCount||0;